# Definition for a binary tree node.# class TreeNode:#     def __init__(self, val=0, left=None, right=None):#         self.val = val#         self.left = left#         self.right = rightclass Solution:    def isSymmetric(self, root: TreeNode) -> bool:        if root is None:            return True        else:            return self.compareChild(root.left, root.right)            def compareChild(self, left: TreeNode, right: TreeNode):        if left is None and right is None:            return True        elif left is None or right is None:            return False        else:            if left.val == right.val:                return self.compareChild(left.left, right.right) and self.compareChild(left.right, right.left)            return False